
function s2t(string){
	
	let transWordStr = ""
	let i = 0
	// 字轉換
	while(i < string.length){
		const fetchWord = string[i]
		if(wordDict.hasOwnProperty(fetchWord)){
			transWordStr += wordDict[fetchWord]
		}
		else{
			transWordStr += fetchWord
		}
		i += 1
	}
	// 詞轉換
	let transPhraseStr = ""
	i = 0
	while(i < transWordStr.length){
		const firstWord = transWordStr[i]

		let findPhraseFlag = false

		if(phraseDict.hasOwnProperty(firstWord)){
			
			for(const phraseLen in phraseDict[firstWord]){
				if(!(phraseLen.match(/^\d+$/))){continue}
				phraseLen = Number(phraseLen)
			
				const fetchStr = transWordStr.substr(i,phraseLen)

				if(phraseDict[firstWord][phraseLen].hasOwnProperty(fetchStr)){
					
					transPhraseStr += phraseDict[firstWord][phraseLen][fetchStr]
					i += phraseLen
					findPhraseFlag = true
					break
				}
			}
			if(findPhraseFlag){
				continue
			}
		}
		
		transPhraseStr += firstWord
		i+=1
	}
	return(transPhraseStr)
}


